import { Gift, Star, Clock, Truck } from 'lucide-react';

const trustItems = [
  {
    icon: Gift,
    title: 'Free Tester',
    description: 'With Every Order',
  },
  {
    icon: Star,
    title: 'High Quality',
    description: 'Same As Original',
  },
  {
    icon: Clock,
    title: 'Long Lasting',
    description: 'Premium Fragrance',
  },
  {
    icon: Truck,
    title: 'Fast Delivery',
    description: 'All Over Lebanon',
  },
];

const TrustSection = () => {
  return (
    <section className="py-12 border-y border-border/50 bg-card/30">
      <div className="container">
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-6 md:gap-8">
          {trustItems.map((item, index) => (
            <div
              key={item.title}
              className="flex flex-col items-center text-center p-4 animate-fade-up"
              style={{ animationDelay: `${index * 100}ms` }}
            >
              <div className="h-14 w-14 rounded-full bg-gold/10 flex items-center justify-center mb-4 gold-glow">
                <item.icon className="h-6 w-6 text-gold" />
              </div>
              <h3 className="font-serif text-lg font-semibold text-foreground mb-1">
                {item.title}
              </h3>
              <p className="text-muted-foreground text-sm">{item.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TrustSection;
