import { Gift } from 'lucide-react';

const AnnouncementBar = () => {
  return (
    <div className="bg-gradient-to-r from-gold-dark via-gold to-gold-dark py-2.5 px-4">
      <div className="container flex items-center justify-center gap-2 text-primary-foreground">
        <Gift className="h-4 w-4 animate-pulse" />
        <p className="text-xs sm:text-sm font-medium tracking-wide uppercase">
          Free Tester With Every Order
        </p>
        <Gift className="h-4 w-4 animate-pulse" />
      </div>
    </div>
  );
};

export default AnnouncementBar;
