import { memo } from 'react';
import { ShoppingCart, Star } from 'lucide-react';
import { Product, useCart } from '@/contexts/CartContext';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import OptimizedImage from '@/components/ui/OptimizedImage';

interface ProductCardProps {
  product: Product;
  priority?: boolean;
}

const ProductCard = memo(({ product, priority = false }: ProductCardProps) => {
  const { addToCart } = useCart();
  const { toast } = useToast();

  const handleAddToCart = () => {
    addToCart(product);
    toast({
      title: 'Added to Cart',
      description: `${product.name} has been added to your cart.`,
    });
  };

  const categoryLabel = {
    men: 'For Him',
    women: 'For Her',
    bestseller: 'Bestseller',
  };

  return (
    <div className="group glass-card rounded-xl overflow-hidden transition-shadow duration-200 hover:shadow-lg hover:shadow-gold/10">
      {/* Image with lazy loading */}
      <div className="relative overflow-hidden">
        <OptimizedImage
          src={product.image}
          alt={product.name}
          priority={priority}
          aspectRatio="square"
          className="transition-transform duration-300 group-hover:scale-105"
        />
        
        {/* Category Badge */}
        <div className="absolute top-3 left-3 z-10">
          <span className="px-3 py-1 text-xs font-medium bg-gold/90 text-primary-foreground rounded-full">
            {categoryLabel[product.category]}
          </span>
        </div>

        {/* Bestseller Star */}
        {product.category === 'bestseller' && (
          <div className="absolute top-3 right-3 z-10">
            <Star className="h-5 w-5 text-gold fill-gold" />
          </div>
        )}
      </div>

      {/* Content */}
      <div className="p-4 space-y-3">
        <div>
          <h3 className="font-serif text-lg font-semibold text-foreground group-hover:text-gold transition-colors duration-200">
            {product.name}
          </h3>
          {product.description && (
            <p className="text-muted-foreground text-sm mt-1 line-clamp-2">
              {product.description}
            </p>
          )}
        </div>

        <div className="flex items-center justify-between pt-2">
          <span className="text-xl font-bold text-gold">${product.price}</span>
          <Button
            variant="goldOutline"
            size="sm"
            onClick={handleAddToCart}
          >
            <ShoppingCart className="h-4 w-4 mr-1" />
            Add
          </Button>
        </div>
      </div>
    </div>
  );
});

ProductCard.displayName = 'ProductCard';

export default ProductCard;
