  },
  {
    id: 'men-4',
    name: 'Tobacco Vanille',
    price: 55,
    image: menPerfume1,
    category: 'men',
    description: 'Warm tobacco and vanilla blend for evening occasions'
  },
  {
    id: 'men-5',
    name: 'Savage Legend',
