  {
    id: 'women-2',
    name: 'Velvet Orchid',
    price: 55,
    image: womenPerfume3,
    category: 'women',
    description: 'Exotic orchid bloom with creamy vanilla'
  },
  {
    id: 'women-3',
    name: 'Cherry Blossom',
    price: 42,
    image: womenPerfume1,
    category: 'women',
    description: 'Light floral with Japanese cherry notes'
