  {
    id: 'best-2',
    name: 'Eclat Signature',
    price: 75,
    image: bestseller2,
    category: 'bestseller',
    description: 'The signature house scent of Eclat Parfum'
  },
  {
    id: 'best-3',
    name: 'Beirut Nights',
    price: 60,
    image: bestseller1,
    category: 'bestseller',
    description: 'Inspired by the magic of Beirut after dark'
  },
  {
    id: 'best-4',
    name: 'Cedar Mountain',
    price: 58,
    image: bestseller2,
    category: 'bestseller',
    description: 'Lebanese cedar with fresh mountain air'
  },
];

export const getProductsByCategory = (category: 'men' | 'women' | 'bestseller') => {
  return products.filter((product) => product.category === category);
};

export const getProductById = (id: string) => {
  return products.find((product) => product.id === id);
};
